///////////////////////////////		
// Set Variables
///////////////////////////////
var catptionOffset = -20;
var thumbWidth = 350;
var widgetsHidden = false;
var themeColumns = 3;

///////////////////////////////		
// Mobile Detection
///////////////////////////////

function isMobile(){
    return (
        (navigator.userAgent.match(/Android/i)) ||
		(navigator.userAgent.match(/webOS/i)) ||
		(navigator.userAgent.match(/iPhone/i)) ||
		(navigator.userAgent.match(/iPod/i)) ||
		(navigator.userAgent.match(/iPad/i)) ||
		(navigator.userAgent.match(/BlackBerry/))
    );
}


///////////////////////////////
// Center Home Flexslider Text
///////////////////////////////

function centerFlexCaption() {
	jQuery('.home .slideshow .details').each(function(id){		
		jQuery(this).css('margin-top','-'+((jQuery(this).actual('height')/2)-catptionOffset)+'px');		
	});
}

///////////////////////////////
// Mobile Nav
///////////////////////////////

function setMobileNav(){
	jQuery('#mainNav .sf-menu').tinyNav({
		header: 'Navigation',
	    active: 'current-menu-item'
	});	
}

///////////////////////////////
// Initialize
///////////////////////////////	
	
jQuery.noConflict();
jQuery(window).load(function(){
	jQuery(".videoContainer").fitVids();
	centerFlexCaption();
	setMobileNav();
	//setMobileNav();	
	jQuery(window).on("debouncedresize", function( event ) {
		centerFlexCaption();	
	});
	jQuery('img').attr('title','');	
});



jQuery(window).load(function() {
  jQuery('.home #header .flexslider').flexslider({
    slideshowSpeed: 6000,
    directionNav: true,
    slideshow: 1,
    animation: 'fade',
    animationLoop: true
  });  
});

jQuery(window).load(function() {
  jQuery('#content .flexslider').flexslider({
    directionNav: true,
    slideshow: 0,
    animation: 'fade',
    animationLoop: true
  });  
});

jQuery(window).load(function(){
  jQuery('#content .cItems').infinitescroll({

    navSelector  : "div.pagination",
    nextSelector : "div.pagination a:first",
    itemSelector : "#content .cItems div.hentry",
    loading: {
      finishedMsg: "",
      img: "",
      msgText: ""
    },
    errorCallback: function() {   
      jQuery('.infscrBtn').animate({opacity: 0.8},2000).fadeOut('normal');
    }},     
    function( newElements ) {
      var newElems = jQuery(newElements);
      newElems.hide();
      newElems.imagesLoaded(function(){
        jQuery(".videoContainer").fitVids();
              
        jQuery('#content .flexslider').flexslider({
          directionNav: true,
          slideshow: 0,
          animation: 'fade',
          animationLoop: true
        });  
        
        newElems.fadeIn('slow');
        
      });
      var opts = jQuery('#content .cItems').data('infinitescroll').options;
      
      if(opts.state.currPage == 2){       
        jQuery('.infscrBtn').fadeOut('normal');
      }else{
        jQuery('.infscrBtn span').text("Load More"); 
      } 
    }
  );
  

  jQuery(window).unbind('.infscr');
  
  jQuery('.cItems div.hentry').css("display: none;");
  
  // hook up the manual click.
  jQuery('.infscrBtn').click(function(){
    jQuery('#content .cItems').infinitescroll('retrieve');
    jQuery('.infscrBtn span').text("Loading...");
  }); 
  
});
